    'worklet';
    const diff = {};
    for (const key in oldStyle) {
        if (newStyle[key] === undefined) {
            diff[key] = null;
        }
    }
    for (const key in newStyle) {
        const value = newStyle[key];
        const oldValue = oldStyle[key];
